def has_russian_text(text):
    """ĞŸÑ€Ğ¾Ğ²ĞµÑ€ÑĞµÑ‚, ÑĞ¾Ğ´ĞµÑ€Ğ¶Ğ¸Ñ‚ Ğ»Ğ¸ Ñ‚ĞµĞºÑÑ‚ Ñ€ÑƒÑÑĞºĞ¸Ğµ ÑĞ¸Ğ¼Ğ²Ğ¾Ğ»Ñ‹"""
    russian_pattern = re.compile(r'[Ğ°-ÑÑ‘]', re.IGNORECASE)
    return bool(russian_pattern.search(text))

