def fix_translations():
    l10n_dir = 'lib/l10n'
    
    for lang_file in languages:
        file_path = os.path.join(l10n_dir, lang_file)
        
        if not os.path.exists(file_path):
            print(f"‚ö†Ô∏è  –§–∞–π–ª {lang_file} –Ω–µ –Ω–∞–π–¥–µ–Ω, –ø—Ä–æ–ø—É—Å–∫–∞–µ–º")
            continue
            
        try:
            with open(file_path, 'r', encoding='utf-8') as f:
                data = json.load(f)
            
            # –ü—Ä–æ–≤–µ—Ä—è–µ–º, –µ—Å—Ç—å –ª–∏ –∫–ª—é—á –∏ —Å–æ–¥–µ—Ä–∂–∏—Ç –ª–∏ –æ–Ω —Ä—É—Å—Å–∫–∏–π —Ç–µ–∫—Å—Ç
            if 'five_cards_screen_prompt' in data:
                current_value = data['five_cards_screen_prompt']
                if any(ord(char) > 127 for char in current_value) and any(char in '–∞-—è—ë' for char in current_value.lower()):
                    # –ó–∞–º–µ–Ω—è–µ–º –Ω–∞ –ø—Ä–∞–≤–∏–ª—å–Ω—ã–π –ø–µ—Ä–µ–≤–æ–¥
                    data['five_cards_screen_prompt'] = translations[lang_file]
                    
                    # –°–æ—Ö—Ä–∞–Ω—è–µ–º —Ñ–∞–π–ª
                    with open(file_path, 'w', encoding='utf-8') as f:
                        json.dump(data, f, ensure_ascii=False, indent=2)
                    
                    print(f"‚úÖ –ò—Å–ø—Ä–∞–≤–ª–µ–Ω {lang_file}")
                else:
                    print(f"‚ÑπÔ∏è  {lang_file} —É–∂–µ —Å–æ–¥–µ—Ä–∂–∏—Ç –ø—Ä–∞–≤–∏–ª—å–Ω—ã–π –ø–µ—Ä–µ–≤–æ–¥")
            else:
                print(f"‚ö†Ô∏è  –ö–ª—é—á 'five_cards_screen_prompt' –Ω–µ –Ω–∞–π–¥–µ–Ω –≤ {lang_file}")
                
        except Exception as e:
            print(f"‚ùå –û—à–∏–±–∫–∞ –ø—Ä–∏ –æ–±—Ä–∞–±–æ—Ç–∫–µ {lang_file}: {e}")

if __name__ == "__main__":
    print("üîß –ò—Å–ø—Ä–∞–≤–ª–µ–Ω–∏–µ –ø–µ—Ä–µ–≤–æ–¥–æ–≤ –¥–ª—è —Å–ª–µ–¥—É—é—â–∏—Ö 10 —è–∑—ã–∫–æ–≤...")
    fix_translations()
    print("‚ú® –ì–æ—Ç–æ–≤–æ!") 